linit 1

SELECT t.trade_id tradeId, t.trade_acc_id tradeAccId, t.trade_player_id playerId, t.trade_order_id tradeOrderId, t.trade_amount tradeAmount, t.trade_type tradeType, t.trade_amount_type tradeAmountType, t.trade_desc tradeDesc,t.create_time createTime, t.update_time updateTime,p.player_name playerName,p.player_nick playerNick FROM player_trade t LEFT JOIN city_player p ON t.trade_player_id = p.player_id
1=1
and t.trade_id = #{tradeId,jdbcType=INTEGER}
and t.trade_acc_id = #{accId,jdbcType=INTEGER}
and t.trade_order_id = #{orderId,jdbcType=INTEGER}
and t.trade_type = #{tradeType,tradeType=CHAR},
and t.trade_amount_type = #{tradeAmountType,jdbcType=CHAR}
and t.trade_desc LIKE '%${tradeDesc}%'
and p.player_name LIKE '%${playerName}%'
and p.player_nick LIKE '%${playerNick}%'
and t.create_time >= STR_TO_DATE(#{createTimeStart,jdbcType=VARCHAR},'%Y-%m-%d %h:%m:%s')
and t.create_time <= STR_TO_DATE(#{createTimeEnd,jdbcType=VARCHAR},'%Y-%m-%d %h:%m:%s')
order by t.create_time desc
